package com.syntax.class01;

public class PrimitivedDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//storing and representing whole numeric values;
		byte box1 =127;
		
		short box2=32767;
		
		int box3=100000; // popular
		
		long box4=1000000000l;// credit cards,
		
		//Storing decimals
		float variable1= 1.99F; //about 6 decimal digits
		
		double variable2=99.99; //about 15 decimal digits 
		
		//storing single characters
		
		char container='a';
		char dollar='$';
		
		// storing yes or no values
		
		boolean hot= true;
		boolean cold= false;
		
		System.out.println(variable2);//printing value of a variable 
		System.out.println("variable2");//printing literal text
		//HW1 below
		System.out.println(box1);
		System.out.println(box2);
		System.out.println(box3);
		System.out.println(box4);
		System.out.println(variable1);
		System.out.println(variable2);
		System.out.println(container);
		System.out.println(dollar);
		System.out.println(hot);
		System.out.println(cold);
		
		int age; 
		age=4;
		
		
		
	}

}
